import React, { useEffect, useState } from 'react';
import './Styles/MainPage.css';

import ReactDOM from 'react-dom';
import { checkPropTypes } from 'prop-types';

const MainPage = (props) => {
    //data structure for getting images
    const [imgDataList,setImgDataList] = useState([]);
    //data structure for getting tags
    const [tagDataList,setTagDataList] = useState([]);
    //variable to store user imput tag name
    const [tagText, setTagText] = useState("");

    //map to store binding between tag id and object
    const [tagIDTable, setTagIDTable] = useState({});
    //variable to store selected tag
    const [tag,setTag] = useState(-1);

    // let tagIDTable = {};

    // Fetch all img data
    // console.log(localStorage.getItem("Authorization"));
    useEffect(() => {
        fetch("https://api.xutiancheng.me/v1/photos",  { 
            method: 'get', 
            headers: new Headers({
                // 'Authorization': "Bearer 6Ktu0thS3MI4dDnE8HY7TH_PMybp0xiEXTobOSh5LFdt28-ma7TAKgPg04slyj40TI8qjpI8LSgoishrdViUyg==",
                'Authorization': localStorage.getItem("Authorization"), 
            })
          })
        .then(resp => resp.json())
        // .then(data => {setImgDataList(data)})
    });
    // Fetch all tag data
    useEffect(() => {
        fetch("https://api.xutiancheng.me/v1/tags",  { 
            method: 'get', 
            headers: new Headers({
                // 'Authorization': "Bearer 6Ktu0thS3MI4dDnE8HY7TH_PMybp0xiEXTobOSh5LFdt28-ma7TAKgPg04slyj40TI8qjpI8LSgoishrdViUyg==",
              'Authorization': localStorage.getItem("Authorization"), 
            })
          })
        .then(resp => resp.json())
        // .then(data => 
        // {   
        //     // console.log(data.length);
        //     // setTagIDTable(data);
        //     // console.log(tagIDTable);
        //     setTagDataList(data);
        //     //setTagIDTable(data)
        // })


    });

    // function setTagIDTable(data) {
    //     console.log("tagIDTable");
    //     for (let index = 0; index < data.length; index++) {
    //         const element = data[index];
    //         let obj={};
    //         obj[element.id] = element.name;
    //         Object.assign(tagIDTable, obj);

    //     }
    // }

    // //TODO:
    // //assign tag id and name to the tag id table
    // let setTagIDTable = (data) => {
    //     Object.assign(tagIDTable, {data.id : data});
    // }

    return (
    <div>
        <TagButtonList tags={tagDataList} onClick={setTag}/>
        <ImgCardList tag={tag} imgDataList={imgDataList} tagIDTable={tagIDTable}/>
        {/* <ImgCardList tags={tagDataList} tag={tag} imgDataList={imgDataList}/> */}
        <TagTextField></TagTextField>
    </div>
    );

}

//populates the tag buttons into a list
const TagButtonList = (props)=>{
    const TagLists = props.tags.map(data=><TagButton key={data.id} tag={data} onClick={props.onClick}/>);
    return (<div style={{display:"flex","flexDirection": "row"}}>{TagLists}</div>)
}

//TODO:
//gets the new tag name typed by user, after user press 'Enter', pass it to /v1/tags
//with payload: {"name": "tagname"}
const TagTextField = () => {
    return <input
    type="text"
    placeholder="type tag to upload..."
    onChange={event => {this.setState({tagText: event.target.value})}}
    onKeyPress={event => {
                if (event.key === 'Enter') {
                    useEffect(() => {
                        // POST request using fetch inside useEffect React hook
                        const requestOptions = {
                            method: 'POST',
                            body: JSON.stringify({ name: "tagText" })
                        };
                        // fetch("https://api.xutiancheng.me/v1/tags", requestOptions)
                        //     .then(response => response.json())
                        //     .then(console.log(response));

                    // empty dependency array means this effect will only run once (like componentDidMount in classes)
                    }, []);
                }
              }}
    />;
};

//individual button showing each tag
const TagButton = (props)=>{
        return <button onClick={props.tag.id}>{props.tag.name}</button>;

    // return <button onClick={props.onClick(props.tag.id)}>props.tag.name</button>;
}

//populates the image cards into a list
const ImgCardList = (props) => {
    var photoListItems;
    if (props.tag === -1) {
        photoListItems = props.imgDataList.map((data) => {
            // return <ImgCard key={data.id} img={data} tags={props.tags}/>

            return <ImgCard key={data.id} img={data}  tagIDTable={tagIDTable}/>
        });
    } else {
        photoListItems = props.imgDataList.map((data) => {
            if (data.tags.includes(props.tag)){
                // return <ImgCard key={data.id} img={data} tags={props.tags}/>

                return <ImgCard key={data.id} img={data} style={{display:"flex","flex-wrap":"wrap","flexDirection": "row"}}  tagIDTable={tagIDTable}/>
            }
        });
    }

    return (photoListItems)
}

//individual image card, still missing function that when clicked,
//lets user add a tag for this photo
const ImgCard = (props) =>{
    //TODO:
    // let displayTags = props.img.tags.map((item, i) => (
    //     <p>{tagIDTable[item.id].name}</p>
    // ));
    let tagNameList = [];
    for (let index = 0; index < props.img.tags.length; index++) {
        console.log("yahaha");
        let tagID = props.img.tags[index];
        tagNameList.push(tagIDTable.tagID);
    }

    return(
    // <div>
        <img src={props.img.url} style={{width:"100px",height:"100px"}}/>

    //     {/* <button onClick={}>Add Tag</button> */}
    //     {/* {displayTags} */}
    //  {/* </div>); */}
     );


}

